import React from 'react'
import { connect } from 'react-redux';

const associatedComments = comments.filter(comment => comment.recipe_id === this.props.recipeId);

const CommentsList = ({associatedComments}) => {
    return(
        <section>
            { associatedComments && associatedComments.map(comment => {
                return (
                    <div className="comment-content">
                        <div className="comment-header">
                            {comment.author}
                        </div>
                        <div className="comment-body">
                            {comment.content}
                        </div>
                        <div className="comment-actions">
                            <div className="helpful-actions">
                                <span className="thumb-icon">
                                    <i class="zmdi zmdi-star-outline"></i>
                                </span>
                                5 
                                This is helpful
                            </div>
                        </div>
                    </div>
                )

            })}
        </section> 
    )
}

const mapStateToProps = state => {
    return {comments: state.comments}
}

export default connect(mapStateToProps)(CommentsList);
