import React from "react";

class CommentsForm extends React.Component {
    state = {
        author: '',
        content: '',
        recipe_id: this.props.recipeId
    }

    handleChange = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        //this.props.createComment(this.state)
        console.log(this.state)
    }

    render() {
        return(

            <div className="form-bg">
                <div className="comment-input">
                    <form className="form" onSubmit={this.handleSubmit}>
                    <input type='text' onChange={this.handleChange} id='author'  placeholder= 'Enter Your Name' />
                        <textarea onChange={this.handleChange} id='content' placeholder= "Enter Tips" />
                        <button className="btn-primary" type='submit'>Add Tip</button>   
                    </form>
                </div>
            </div>
        )
    }

}

export default CommentsForm;
