import React from 'react'
import { connect } from 'react-redux'
import { deleteRecipe } from '../../actions/recipeActions'
import { withRouter } from "react-router";

const RecipeHeader = (props) => {
    const handleDelete = () => {
        props.deleteRecipe(props.recipe.id)
        props.history.push('/')
        this.forceUpdate
    }

    return (
        <header>
            <div className="title-container">
                <h1 className="recipe-title">{props.recipe.name}</h1>
            </div>
            <div className="recipe-controls-primary">
                <button className="btn-warning" onClick={handleDelete}>Delete Recipe</button>
            </div>
        </header>
    )
}

export default withRouter(RecipeHeader)

