import React from "react"
import MainHeader from "../layout/mainHeader"
import MainFooter from "../layout/mainFooter"
import RecipeHeader from "./recipeHeader"
import RecipeMain from "./recipeMain"
import { connect } from 'react-redux'
import { deleteRecipe } from '../../actions/recipeActions'


class Recipe extends React.Component {

    render() { 
        return(
            <div>
                <h1>{this.props.recipe.name}</h1>
            </div>
        )
    }
}

function mapDispatchToProps(dispatch, ownProps) {
    const findId = ownProps.match.params.recipeId
    const id = parseInt(findId, 10)
    return {
        deleteRecipe: () => dispatch(deleteRecipe(id))
    }
}

function mapStateToProps(state, ownProps){
   //const findId = ownProps.match.params.recipeId
    //const id = parseInt(, 10)
    //const recipes = state.recipes
    const recipe = state.recipes ? state.recipes.find(recipe => recipe.id === pareseInt(ownProps.match.params.recipeId, 10)) : null

    return {recipe: recipe}
}

export default connect(mapStateToProps, mapDispatchToProps) (Recipe)

