import { React } from "react";

class CommentsForm extends React.Component {
    state = {
        author: '',
        content: '',
    }

    render() {
        return(
            <form className="form" onSubmit={this.handleSubmit}>
                <input type='text' value={this.state.author} onChange={this.handleChange} id='author' placeholder='Add your name'>
                </input>
                <input type='text' value={this.state.content} onChange={this.handleChange} id='content' placeholder='Tell us your suggestion'>
                </input>
                <input type='submit' value="Add Tip">
                </input>
            </form>
        )
    }

}

export default CommentsForm;
