import React from "react"
import MainHeader from "../layout/MainHeader"
import MainFooter from "../layout/MainFooter"
import RecipeHeader from "./RecipeHeader"
import RecipeMain from "./RecipeMain"
import { connect } from 'react-redux'
//import { fetchRecipes } from '../../actions/recipeActions'


class Recipe extends React.Component {

    //componentDidMount() {
        //this.props.fetchRecipes()
    //}

    handleLoading = () => {
        if (!this.props.recipe){
            return <div>Loading ...</div>
        } else{
            return(
                <div>
                    <MainHeader />
                        <div className="recipe">
                            <RecipeHeader
                                recipeName = {this.props.recipe.name} 
                            />
                            <RecipeMain 
                                recipeDescription = {this.props.recipe.description}
                                recipeDirections = {this.props.recipe.directions}
                                img = {this.props.recipe.img}
                                ingredients = {this.props.recipe.ingredients}
                            />
                        </div>
                    <MainFooter />
                </div>
            )
        }
    }

    render() { 
        console.log(this.props)
        return(
            <div>
                {this.handleLoading()}
            </div>
        )
    }
}

//function mapDispatchToProps(dispatch) {
    //return { fetchRecipes: () => dispatch(fetchRecipes()) }
//}

function mapStateToProps(state, ownProps){
    let recipe = {name: '', img: '', description: '', directions: '', ingredients: []}
    const recipeId = ownProps.match.params.recipeId;
    console.log(state.recipes.recipes.find(recipe => recipe.id === 4))
    if (state.recipes.recipes.length > 0) {
        recipe = Object.assign({}, state.recipes.recipes.find(recipe => recipe.id === 4))

    }
    return {recipe: recipe};
    //const id = ownProps.match.params.recipeId
    //const recipes = state.recipes.recipes
    //const recipe = recipes ? recipes[id -1] : null
    //return {recipe: recipe}
}

export default connect(mapStateToProps) (Recipe)

