import { React } from "react";

class CommentsForm extends React.Component {
    state = {
        author: '',
        content: '',
    }

    render() {
        return(
            <div>
                <div className="comment-input">
                    <form className="form" onSubmit={this.handleSubmit}>
                        <div className="form-container">
                            <div className="author-container">
                                <input type='text' value={this.state.author} onChange={this.handleChange} id='author' placeholder='Add your name'></input>
                            </div>
                            <div className="content-container">
                                <input type='textarea' value={this.state.content} onChange={this.handleChange} id='content' placeholder='Tell us your suggestion'></input>
                            </div>
                            <div className="form-options-container">
                                <div className="form-actions">
                                    <button className="btn-primary" type='submit' value="Add Tip"></button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        )
    }

}

export default CommentsForm;
