import React from 'react'
import { connect } from 'react-redux'
import { deleteRecipes } from '../../actions/recipeActions'

const RecipeHeader = (props) => {
    return (
        <header>
            <div className="title-container">
                <h1 className="recipe-title">{props.recipe.name}</h1>
            </div>
            <div className="recipe-controls-primary">
                <button className="btn-warning" onClick={() => props.delete(props.recipe.id)}>Delete Recipe</button>
            </div>
        </header>
    )
}

export default connect(null, { deleteRecipes })(RecipeHeader)

